import { ORDER_SAVE_SEND_COUNTRY } from '../constants/orderConstants'


export const saveSendCountry = (data) => (dispatch) => {
  dispatch({
    type: ORDER_SAVE_SEND_COUNTRY,
    payload: data,
  })

  localStorage.setItem('sendCountry', JSON.stringify(data))
}
