import React, { useState, useMemo} from 'react'
import { useDispatch, useSelector } from 'react-redux'
import Select from 'react-select'
import countryList from 'react-select-country-list'
import { saveSendCountry} from '../actions/orderActions'
import { Form } from 'react-bootstrap'

const CountrySelector = ({history}) => {

  const order = useSelector((state) => state.order)
  const { sendCountry } = order

  console.log(order)

  const [value, setValue] = useState(sendCountry)
  //const [label, setLabel] = useState(sendCountry.value.label)

  const dispatch = useDispatch()

  const changeHandler = (value) => {
    console.log('Infront', value.value)


    setValue(value)

     console.log('Under',value.value)

  }

  const submitHandler = (value) => {

    dispatch(saveSendCountry( value.value))
    //history.push('/payment')
  }

  //const [value, setValue] = useState('')
  const options = useMemo(() => countryList().getData(), [])

//  console.log(options[0].label)
//  console.log(options[0].value)
  //console.log(options)

  console.log('Front', value)



    return (
      <Form onSubmit={submitHandler}>
        <Select 
        className="py-3" 
        options={options} 
        value={value} 
        onChange={changeHandler }
        />

        </Form>

    )
}

export default CountrySelector
