import React, { useEffect, useState } from 'react'
import { LinkContainer } from 'react-router-bootstrap'
import {  Form, Table, Button } from 'react-bootstrap'
import { useDispatch, useSelector } from 'react-redux'
import Message from '../components/Message'
import Loader from '../components/Loader'
import { listOrders} from '../actions/orderActions'


const OrderListScreen = ({ history }) => {
  const dispatch = useDispatch()

  const orderList = useSelector((state) => state.orderList)
  const { loading, error, orders } = orderList



  const userLogin = useSelector((state) => state.userLogin)
  const { userInfo } = userLogin



  useEffect(() => {
    if (userInfo && userInfo.isAdmin) {
      dispatch(listOrders())
    } else {
      history.push('/login')
    }
  }, [dispatch, history, userInfo])


  //Search Box
  const [keyword, setKeyword] = useState('')

  const submitHandler = (e) => {
    e.preventDefault()

    } 





  return (
    <>

    <h1 className="text-center py-3">TRANSACTIONS</h1>

    <Form onSubmit={submitHandler} inline>
      <Form.Control
        type='text'
        name='q'
        onChange={(e) => setKeyword(e.target.value)}
        placeholder='Search Transactions...'
        className='mr-sm-2 ml-sm-5'
      ></Form.Control>
      <Button type='submit' variant='outline-success' className='p-2'>
        Search
      </Button>
    </Form>

      {loading ? (
        <Loader />
      ) : error ? (
        <Message variant='danger'>{error}</Message>
      ) : (

        <Table striped bordered hover responsive className='table-sm'>
          <thead>
            <tr>
              <th>ID</th>

              <th>DATE</th>
              <th>TOTAL TO SEND</th>
              <th>TOTAL SEND + FEE</th>
              <th>RECIPIENT RECEIVES</th>
              <th>RECIPIENT'S NAME</th>
              <th>COUNTRY</th>
              <th>DATE COLLECTED</th>
            </tr>
          </thead>
          <tbody>
            {orders

            .filter(order => order.recipientAddress.lastName === {keyword})

            .map((order) => (
              <tr key={order._id}>
                <td>{order._id}</td>
                <td>{order.createdAt.substring(0, 10)}</td>
                <td>€{order.orderAmount.fromAmount}</td>
                <td>${order.totalAmount}</td>
                <td>${order.orderAmount.toAmount}</td>
                <td>{order.recipientAddress.firstName} {order.recipientAddress.lastName}</td>
                <td>{order.recipientAddress.country}</td>
                <td>{order.createdAt.substring(0, 10)}</td>
                {/* <td>
                  {order && order.isPaid ? (
                    order.paidAt.substring(0, 10) 
                  ) : (
                    <i className='fas fa-times' style={{ color: 'red' }}></i>
                  )}
                </td> */}
                {/* <td>
                  {order && order.isDelivered ? (
                    order.deliveredAt.substring(0, 10)
                  ) : (
                    <i className='fas fa-times' style={{ color: 'red' }}></i>
                  )}
                </td> */}
                {/* <td>
                  <LinkContainer to={`/order/${order._id}`}>
                    <Button variant='light' className='btn-sm'>
                      Details
                    </Button>
                  </LinkContainer>
                </td> */}
              </tr>
            ))}
          </tbody>

        </Table>

      )}


    </>
  )

}

export default OrderListScreen
