import { createStore, combineReducers, applyMiddleware } from 'redux'
import thunk from 'redux-thunk'
import { composeWithDevTools } from 'redux-devtools-extension'
//import  { productListReducer, productDetailsReducer, productDeleteReducer, productCreateReducer, productUpdateReducer, productReviewCreateReducer, productTopRatedReducer   }  from './reducers/productReducers.js'
//import { cartReducer } from './reducers/cartReducers'
import { userLoginReducer, userRegisterReducer} from './reducers/userReducers'
import { ordersendCountryReducer} from './reducers/orderReducers'
import { ordersendAmountReducer} from './reducers/orderReducers'


const reducer = combineReducers({
    userLogin: userLoginReducer,
	userRegister: userRegisterReducer,
	order: ordersendCountryReducer,
	orderAmount: ordersendAmountReducer
})

// RETREIVING USER FROM STORAGE
const userInfoFromStorage = localStorage.getItem('userInfo')
	? JSON.parse(localStorage.getItem('userInfo'))
	: null


// RETREIVING USER FROM STORAGE
const sendCountryFromStorage = localStorage.getItem('sendCountry')
  ? JSON.parse(localStorage.getItem('sendCountry'))
  : {}

  // RETREIVING USER FROM STORAGE
const sendAmountFromStorage = localStorage.getItem('sendAmount')
  ? JSON.parse(localStorage.getItem('sendAmount'))
  : {}

// INITIAL STATE
const initialState = {
	order: {
		sendAmount: sendAmountFromStorage,
		sendCountry: sendCountryFromStorage,
	},
	// orderAmount: {
	// 	sendAmount: sendAmountFromStorage,
	// },
	userLogin: {
		userInfo: userInfoFromStorage,
	},

}


const middleware = [thunk]

const store = createStore(reducer, initialState, composeWithDevTools(applyMiddleware(...middleware)))

export default store
