import React, { useEffect } from 'react'
import { Link } from 'react-router-dom'
import { Button, Row, Col, ListGroup,Card } from 'react-bootstrap'
import { useDispatch, useSelector } from 'react-redux'
import Message from '../components/Message'
import { createOrder } from '../actions/orderActions'
//import { ORDER_CREATE_RESET } from '../constants/orderConstants'
//import { USER_DETAILS_RESET } from '../constants/userConstants'
const PlaceOrderScreen = () => {

     const dispatch = useDispatch()

  const order = useSelector((state) => state.order)

   //   Calculate prices
  const addDecimals = (num) => {
    return (Math.round(num * 100) / 100).toFixed(2)
  }

  // order.sendAmount = addDecimals(
  //   order.sendAmount.reduce((acc, item) => acc + item.sendAmount, 0)
  // )

  console.log('SENDAMOUNT',order.sendAmount )

  order.shippingAmount = addDecimals(order.sendAmount.fromAmount * 0.10, 0)

  order.totalAmount = (
    Number(order.sendAmount.fromAmount) +
    Number(order.shippingAmount )
    ).toFixed(2)

  order.sendAmount.fromAmount = Number(order.sendAmount.fromAmount)

  console.log('N',order.sendAmount.fromAmount)

   //cart.totalPrice = Number(cart.itemsPrice + cart.taxPrice).toFixed(2)
   console.log('TOTAL AMOUNT',order.totaAmount )



  const orderCreate =  useSelector(state => state.orderCreate)
  //const { placeorder, success, error } = orderCreate
   const { success, error } = orderCreate

  useEffect(() => {
      if(success) {
          //history.push(`/order/${order._id}`)
          //history.push('/payment')
          console.log('Transaction: ',success)
        } 
        // eslint-disable-next-line
    }, [history,success])

  const submitTransactionHandler = () => {
    dispatch(createOrder({
        orderAmount: order.sendAmount,
        shippingAmount: order.shippingAmount, //transaction fee
        recipientAddress: order.recipientAddress,
        totalAmount: order.totalAmount, //transaction fee + fromAmount
        exchangeRate: order.exchangeRate,
        sendCountry: order.sendCountry
    }))
  }


    return (
      <>

        <Row>
            <Col md={6}>
            <ListGroup variant='flush'>
            <ListGroup.Item>
              <h2>Recipient Address</h2>
              <p>

                {order.recipientAddress.firName} {order.recipientAddress.middleName} {' '}
                {order.recipientAddress.lastName},{' '}
                {order.recipientAddress.address}, {order.recipientAddress.town},{' '}
                {order.recipientAddress.country}.{' '}
                {order.recipientAddress.email}
                {order.recipientAddress.phone},{' '}
                {order.recipientAddress.reason}


              </p>
            </ListGroup.Item>

            <ListGroup.Item>
              <h2>Payment Method</h2>
              <strong>Method: </strong>
              {/* {order.paymentMethod} */}
            </ListGroup.Item>

                </ListGroup>
                </Col>

                <Col md={6}>
                    <Card>
                        <ListGroup variant='flush'>
                            <ListGroup.Item>
                                <h2>Transaction Summary</h2>
                            </ListGroup.Item>
                            <ListGroup.Item>
                                <Row>
                                    <Col>Paying</Col>
                                    <Col>€{order.sendAmount.fromAmount}</Col>
                                </Row>

                            </ListGroup.Item>
                            <ListGroup.Item>
                                <Row>
                                    <Col>Transaction Fee</Col>
                                    <Col>${order.shippingAmount}</Col>
                                </Row>

                            </ListGroup.Item>
                            <ListGroup.Item>
                                <Row>
                                    <Col>Exchange Rate</Col>
                                    <Col> € 1 = {order.sendAmount.exchangeRate} </Col>
                                </Row>

                            </ListGroup.Item>
                            <ListGroup.Item>
                                <Row>
                                    <Col>Total Amount</Col>
                                    <Col>${order.totalAmount}</Col>
                                </Row>

                            </ListGroup.Item>
                            <ListGroup.Item>
                                <Row>
                                    <Col>Your Receiver Gets</Col>
                                    <Col>€{order.sendAmount.toAmount}</Col>
                                </Row>

                            </ListGroup.Item>
                            <ListGroup.Item>
                                {error && <Message variant='danger'>{error}</Message>}
                            </ListGroup.Item>
                            <ListGroup.Item>
                              {/* <Button type='button' className='btn-block' disabled={order.cartItems === 0 } */}
                              <Button type='button' className='btn-block' disabled={order.cartItems === 0 }
                                onClick={submitTransactionHandler}>Submit Transaction</Button>

                            </ListGroup.Item>

                        </ListGroup>
                    </Card>
                </Col>
      </Row>
    </>
  )



}

export default PlaceOrderScreen
