import { ORDER_CREATE_REQUEST,
  ORDER_CREATE_SUCCESS,
  ORDER_CREATE_RESET,
  ORDER_CREATE_FAIL,
  ORDER_SAVE_SEND_COUNTRY, 
  ORDER_AMOUNT, 
  SAVE_RECIPIENT_ADDRESS,
  SAVE_COLLECTION_METHOD
} from '../constants/orderConstants'




export const orderCreateReducer = (state = {}, action) => {
  switch (action.type) {
    case ORDER_CREATE_REQUEST:
      return {
        loading: true,
      }
    case ORDER_CREATE_SUCCESS:
      return {
        loading: false,
        success: true,
        placeorder: action.payload,
      }
    case ORDER_CREATE_FAIL:
      return {
        loading: false,
        error: action.payload,
      }
    case ORDER_CREATE_RESET:
      return {}
    default:
      return state
  }
}


export const orderReducer = (state = {sendCountry: {}, sendAmount: {}, shippingAddress: {}, collectionMethod: {}, }, action) => {

    switch(action.type) {
        case ORDER_SAVE_SEND_COUNTRY: 
                    return {
                        ...state,
                        sendCountry: action.payload

                    }



        case ORDER_AMOUNT:
                    return {
                        ...state,
                        sendAmount: action.payload

                    }


      case SAVE_RECIPIENT_ADDRESS: 
        return {
          ...state,
          recipientAddress: action.payload

			}

      case SAVE_COLLECTION_METHOD: 
                    return {
                        ...state,
                        collectionMethod: action.payload

                    }

        default: return state
                }  



    }
