import {ORDER_SAVE_SEND_COUNTRY, ORDER_AMOUNT} from '../constants/orderConstants'


export const ordersendCountryReducer = (state = {sendCountry: {}  }, action) => {
    switch(action.type) {
        case ORDER_SAVE_SEND_COUNTRY: 
                    return {
                        ...state,
                        sendCountry: action.payload

                    }

                    default: return state
                }   



    }

    export const ordersendAmountReducer = (state = {sendAmount: {}  }, action) => {
    switch(action.type) {
        case ORDER_AMOUNT:
                    return {
                        ...state,
                        sendAmount: action.payload

                    }

                    default: return state
                }   



    }
