  const changeHandler = value => {
     dispatch(saveSendCountry(setValue(value)))
    // setValue(value)
     //console.log(value.label)
     //console.log(value.value)

  }

    return (
        <Select 
        className="py-3" 
        options={options} 
        value={value} 
        onChange={changeHandler} 
        //onChange={(e) => setValue(e.target.value)}
        />

    )
}

export default CountrySelector
